sys_script_include.script:
var SendRestMessage = Class.create();
SendRestMessage.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getRestMessage: function() {
		gs.info("*TRACE* SendRestMessage Start");
		var requestBody;
		var responseBody;
		var status;
		var sm;
		try{
			sm = new sn_ws.RESTMessageV2("incident", "get");
			sm.setBasicAuth("ibm_ban_t","aflacgmt");
			sm.setMIDServer('MIDServer1');
			response = sm.execute();
			response.waitForResponse(60);
			responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
			status = response.getStatusCode();
		} catch(ex) {
			responseBody = ex.getMessage();
			gs.info("Request Body: " + requestBody);
			status = '500';
		} finally {
			requestBody = sm ? sm.getRequestBody():null;
		}
		gs.info("Request Body: " + requestBody);
		gs.info("Response: " + responseBody);
		gs.info("HTTP Status: " + status);

// 		return "SendRestMessage Ended.";
		return responseBody;
	}
});
