// 			response.waitForResponse(20);
// 			responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
// 			status = response.getStatusCode();
			responseBody = '{"service":{"name":"prodlib","scope":[{"name":"service","message":[{"name":"list","header":[{}],"result":[{"applName":[{}],"backupLib":[{}],"hfsLib":[{}],"libType":[{}],"prodLib":[{}],"siteName":"A0","tempLib":[{}]},{"applName":[{}],"backupLib":[{}],"hfsLib":[{}],"libType":[{}],"prodLib":[{}],"siteName":"A4","tempLib":[{}]}],"response":[{}]}]}]}}';

			//JSON String Representation of siteName
