var CreatePackage1 = Class.create();
CreatePackage1.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getRestMessage: function() {
		gs.info("*TRACE* CreatePackage1 Start");
		var requestBody;
		var responseBody;
		var status;
		var sm;
		try{
			sm = new sn_ws.RESTMessageV2("createPackage1", "get");
			sm.setBasicAuth("ibm_ban_t","aflacgmt");
			sm.setMIDServer('MIDServer1');
			sm.setStringParameter("ZddServer","SERU");
			sm.setStringParameter("ZddChangeMan","CMNU");
			sm.setStringParameter("ChangeManLogin","B12345");
			sm.setStringParameter("ChangeManPassword","password");
			sm.setStringParameter("ItemID","20171208-002");
			response = sm.execute();
			response.waitForResponse(20);
			responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
			status = response.getStatusCode();
		} catch(ex) {
			responseBody = ex.getMessage();
			gs.info("Request Body: " + requestBody);
			status = '500';
		} finally {
			requestBody = sm ? sm.getRequestBody():null;
		}
		//JSON String Representation of Clothing items
		var str = '{"name":"George","lastname":"Washington"}';
		var parser = new global.JSON();
		var obj = parser.decode(str);
		gs.info('The first name is '  + obj.name);

