// 		rec.u_work_request_id = '20171108-001'; 
// 		rec.insert();

		gs.info("Request Body: " + requestBody);
		gs.info("Response: " + responseBody);
		gs.info("HTTP Status: " + status);

		return "CreatePackage1 Ended.";
	}
});
