sys_script_include.script:
var ScriptIncludeTest = Class.create();
ScriptIncludeTest.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getRestMessage: function() {
// 		gs.info("LOG TRACE1");
		var requestBody;
		var responseBody;
		var status;
		var sm;
		try{
// 			gs.info("LOG TRACE2");
			sm = new sn_ws.RESTMessageV2("incident", "get");
// 			gs.info("LOG TRACE3");
			sm.setBasicAuth("ibm_ban_t","aflacgmt");
			// sm.setStringParameter("symbol", "NOW");
			// sm.setStringParameterNoEscape("xml_data","<data>test</data>");
			sm.setHttpTimeout(10000); //In milliseconds. Wait at most 10 seconds for response from http request.

// 			gs.info("LOG TRACE4");
			response = sm.execute();
			responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
			status = response.getStatusCode();
		} catch(ex) {
// 			gs.info("LOG TEST5 catch");
			responseBody = ex.getMessage();
			status = '500';
		} finally {
			requestBody = sm ? sm.getRequestBody():null;
		}
		gs.info("Request Body: " + requestBody);
		gs.info("Response: " + responseBody);
		gs.info("HTTP Status: " + status);

		return "Server Side Script Ended.";
	}
});
